class VoiceAssistantScreen extends StatefulWidget {
  const VoiceAssistantScreen({super.key});

  @override
  State<VoiceAssistantScreen> createState() => _VoiceAssistantScreenState();
}

class _VoiceAssistantScreenState extends State<VoiceAssistantScreen>
    with TickerProviderStateMixin {
  bool isRecording = false;
  bool isProcessing = false; // 处理中状态
  bool _isCanceling = false; // 取消状态
  bool _isTouching = false; // 是否正在按住
  String recordingText = '';
  DateTime? _recordingStartTime;
  List<VoiceSession> sessions = [];
  List<VoiceSession> historySessions = [];
  String? editingTaskId;
  final Map<String, String> _tempParamValues = {}; // 备份参数值用于取消编辑
  final Map<String, TextEditingController> _paramControllers = {}; // 编辑模式下的控制器

  // 模拟模式：设为 true 可绕过麦克风，直接使用模拟音频测试
  static const bool _debugMockMode = false;

  late final AudioRecorder _audioRecorder;
  late AnimationController _bounceController;
  late AnimationController _spinController;
  late final ScrollController _scrollController;

  @override
  void initState() {
    super.initState();
    _loadHistory();
    _audioRecorder = AudioRecorder();
    _scrollController = ScrollController();
    _bounceController = AnimationController(
      vsync: this,
      duration: const Duration(milliseconds: 600),
    )..repeat();
    _spinController = AnimationController(
      vsync: this,
      duration: const Duration(seconds: 4),
    )..repeat();
  }

  Future<void> _loadHistory() async {
    try {
      final prefs = await SharedPreferences.getInstance();
      final historyJson = prefs.getStringList('voice_history') ?? [];
      if (!mounted) return;
      setState(() {
        historySessions = historyJson
            .map((e) => VoiceSession.fromJson(jsonDecode(e)))
            .toList();
      });
    } catch (e) {
      debugPrint('Error loading history: $e');
    }
  }

  Future<void> _saveSession(VoiceSession session) async {
    try {
      final prefs = await SharedPreferences.getInstance();

      final updatedHistory = List<VoiceSession>.from(historySessions);
      updatedHistory.insert(0, session);
      if (updatedHistory.length > 30) {
        updatedHistory.removeLast();
      }

      if (mounted) {
        setState(() {
          historySessions = updatedHistory;
        });
      }

      final historyJson =
          updatedHistory.map((e) => jsonEncode(e.toJson())).toList();
      await prefs.setStringList('voice_history', historyJson);
    } catch (e) {
      debugPrint('Error saving history: $e');
    }
  }

  @override
  void dispose() {
    _audioRecorder.dispose();
    _bounceController.dispose();
    _spinController.dispose();
    _scrollController.dispose();
    for (final controller in _paramControllers.values) {
      controller.dispose();
    }
    _paramControllers.clear();
    super.dispose();
  }

  Future<bool> _checkPermission() async {
    if (kIsWeb) {
      return true; // Web handles permission via browser prompt on start()
    }

    if (Platform.isWindows || Platform.isMacOS || Platform.isLinux) {
      // Desktop platforms might handle permissions differently or not support permission_handler fully
      // assuming granted or handled by OS
      return true;
    }
    var status = await Permission.microphone.status;
    if (status.isDenied) {
      status = await Permission.microphone.request();
    }
    return status.isGranted;
  }

  // 录音开始
  Future<void> handleTouchStart() async {
    if (isProcessing || isRecording) {
      ToastUtils.show('正在处理，请稍候');
      return;
    }

    // 模拟模式：直接进入录音状态，不需要麦克风
    if (_debugMockMode) {
      setState(() {
        isRecording = true;
        _isCanceling = false;
        _recordingStartTime = DateTime.now();
        recordingText = '';
      });
      return;
    }

    final hasPermission = await _checkPermission();
    if (!hasPermission) {
      ToastUtils.showWarning('需要麦克风权限才能使用语音功能');
      return;
    }

    // 如果在请求权限过程中松手了，则不开始录音
    if (!_isTouching) {
      return;
    }

    try {
      String path;
      if (kIsWeb) {
        path = 'voice_command_${DateTime.now().millisecondsSinceEpoch}.wav';
      } else {
        final dir = await getTemporaryDirectory();
        path =
            '${dir.path}/voice_command_${DateTime.now().millisecondsSinceEpoch}.wav';
      }

      await _audioRecorder.start(
        const RecordConfig(encoder: AudioEncoder.wav),
        path: path,
      );

      setState(() {
        isRecording = true;
        _isCanceling = false;
        _recordingStartTime = DateTime.now();
        recordingText = '正在听...';
      });
      _scrollToBottom();
    } catch (e) {
      debugPrint('Start recording error: $e');
      ToastUtils.showError('无法启动录音: $e');
    }
  }

  // 录音结束并发送请求
  Future<void> handleTouchEnd() async {
    if (!isRecording) return;

    // 检查录音时长
    final duration =
        DateTime.now().difference(_recordingStartTime ?? DateTime.now());
    if (duration.inMilliseconds < 500) {
      await _audioRecorder.stop();
      setState(() {
        isRecording = false;
        recordingText = '';
      });
      ToastUtils.show('说话时间太短');
      return;
    }

    try {
      String audioBase64;

      // 模拟模式：使用空音频数据
      if (_debugMockMode) {
        setState(() {
          isRecording = false;
          isProcessing = true;
          recordingText = '正在分析指令...（模拟模式）';
        });
        // 生成一个简单的模拟音频数据（空WAV）
        // audioBase64 =
        //     '';
        // ;
        // audioBase64 =
        //     '';
        audioBase64 = '';
      } else {
        final path = await _audioRecorder.stop();

        setState(() {
          isRecording = false;
          isProcessing = true;
          recordingText = '正在分析指令...';
        });

        if (path == null) {
          throw Exception('录音文件生成失败');
        }

        if (kIsWeb) {
          final response = await http.get(Uri.parse(path));
          if (response.statusCode == 200) {
            audioBase64 = base64Encode(response.bodyBytes);
          } else {
            throw Exception('Failed to read blob: ${response.statusCode}');
          }
        } else {
          final file = File(path);
          if (!await file.exists()) {
            throw Exception('录音文件不存在');
          }

          final bytes = await file.readAsBytes();
          audioBase64 = base64Encode(bytes);

          await file.delete().catchError((e) => file);
        }
      }

      // 2. 调用 API
      final result = await VoiceApi.sendVoice(audioBase64: audioBase64);

      if (!mounted) return;

      if (result.isSuccess && result.data != null) {
        final data = result.data!;

        // 转换任务
        final newTasks = (data.functions).map((func) {
          // 转换参数
          final params = func.arguments.entries.map((entry) {
            return TaskParam(
              key: entry.key,
              value: entry.value.value?.toString() ?? '',
              options: entry.value.options,
            );
          }).toList();

          return Task(
            id: DateTime.now().microsecondsSinceEpoch.toString() +
                func.name, // 生成本地唯一ID
            taskId: data.taskId,
            type: func.name,
            description: '执行 ${func.name}', // 默认描述
            parameters: params,
            status: TaskStatus.generated,
            rawFunction: func,
          );
        }).toList();

        if (newTasks.isEmpty && data.audioText.isEmpty) {
          setState(() {
            recordingText = '';
          });
          ToastUtils.show('未识别到有效指令，请重试');
        } else {
          final session = VoiceSession(
            id: DateTime.now().microsecondsSinceEpoch.toString(),
            text: data.audioText.isEmpty ? '识别完成' : data.audioText,
            tasks: newTasks,
            timestamp: DateTime.now(),
          );

          setState(() {
            sessions.add(session); // 改为追加到末尾
            recordingText = ''; // 清除临时状态文本
          });
          _saveSession(session);
          _scrollToBottom();
        }
      } else {
        setState(() {
          recordingText = '识别失败: ${result.message}';
        });
        _scrollToBottom();
      }
    } catch (e) {
      if (mounted) {
        setState(() {
          isRecording = false; // Ensure reset
          recordingText = '发生错误: $e';
        });
        _scrollToBottom();
      }
    } finally {
      if (mounted) {
        setState(() {
          isProcessing = false;
          _recordingStartTime = null;
        });
      }
    }
  }

  void _scrollToBottom() {
    WidgetsBinding.instance.addPostFrameCallback((_) {
      if (_scrollController.hasClients) {
        _scrollController.animateTo(
          _scrollController.position.maxScrollExtent,
          duration: const Duration(milliseconds: 300),
          curve: Curves.easeOut,
        );
      }
    });
  }

  Future<void> _cancelRecording() async {
    if (!isRecording) return;

    try {
      if (!_debugMockMode) {
        final path = await _audioRecorder.stop();
        if (path != null) {
          final file = File(path);
          if (await file.exists()) {
            await file.delete();
          }
        }
      }
    } catch (e) {
      debugPrint('Cancel recording error: $e');
    }

    setState(() {
      isRecording = false;
      recordingText = '';
      _isCanceling = false;
      _recordingStartTime = null;
    });

    ToastUtils.show('已取消录音');
  }

  // 开始编辑任务
  void startEditTask(Task task) {
    setState(() {
      editingTaskId = task.id;
      // 备份当前参数值 并 初始化控制器
      _tempParamValues.clear();
      _paramControllers.clear(); // Ensure clean start
      for (var param in task.parameters) {
        final key = '${task.id}_${param.key}';
        _tempParamValues[key] = param.value;
        _paramControllers[key] = TextEditingController(text: param.value);
      }
    });
  }

  // 保存编辑
  void saveEdit(String taskId) {
    setState(() {
      editingTaskId = null;
      _tempParamValues.clear();
      for (var controller in _paramControllers.values) {
        controller.dispose();
      }
      _paramControllers.clear();
    });
  }

  // 取消编辑
  void cancelEdit() {
    setState(() {
      // 恢复参数值
      if (editingTaskId != null) {
        for (var session in sessions) {
          for (var task in session.tasks) {
            if (task.id == editingTaskId) {
              for (var param in task.parameters) {
                final key = '${task.id}_${param.key}';
                if (_tempParamValues.containsKey(key)) {
                  param.value = _tempParamValues[key]!;
                }
              }
            }
          }
        }
      }
      editingTaskId = null;
      _tempParamValues.clear();
      for (var controller in _paramControllers.values) {
        controller.dispose();
      }
      _paramControllers.clear();
    });
  }

  // 确认执行任务
  Future<void> executeTask(String taskId) async {
    Task? targetTask;
    for (var session in sessions) {
      for (var task in session.tasks) {
        if (task.id == taskId) {
          targetTask = task;
          break;
        }
      }
      if (targetTask != null) break;
    }

    if (targetTask == null) return;
    final task = targetTask;

    setState(() {
      task.status = TaskStatus.executing;
    });

    // 更新原始 Function 的参数值 (从 UI 同步回数据模型)
    for (var param in task.parameters) {
      if (task.rawFunction.arguments.containsKey(param.key)) {
        task.rawFunction.arguments[param.key]?.value = param.value;
      }
    }

    try {
      // 注意：API 需要 command_data 是一个列表。
      // 如果这里是单个任务卡片执行，我们只传这一个任务。
      final result = await VoiceApi.executeCommand(
        taskId: task.taskId,
        commands: [task.rawFunction],
      );

      if (!mounted) return;

      if (result.isSuccess && result.data != null) {
        // 查找对应结果
        // API 返回的是 List<CommandExecutionResult>
        final executionResult = result.data!.firstWhere(
          (element) => element.name == task.type,
          orElse: () => CommandExecutionResult(
              name: task.type, success: false, reason: "No result found"),
        );

        setState(() {
          if (executionResult.success) {
            task.status = TaskStatus.completed;
            task.isExpanded = false;
          } else {
            task.status = TaskStatus.failed;
            // 可以显示失败原因
            ToastUtils.showError('执行失败: ${executionResult.reason}');
          }
        });
      } else {
        setState(() {
          task.status = TaskStatus.failed;
        });
        ToastUtils.showError('请求失败: ${result.message}');
      }
    } catch (e) {
      if (mounted) {
        setState(() {
          task.status = TaskStatus.failed;
        });
        ToastUtils.showError('执行异常: $e');
      }
    }
  }

  // 删除任务
  void deleteTask(String taskId) {
    setState(() {
      for (var session in sessions) {
        session.tasks.removeWhere((t) => t.id == taskId);
      }
      // 如果需要，可以清理空会话，但根据需求保留历史记录可能更好
      // sessions.removeWhere((s) => s.tasks.isEmpty);
    });
  }

  String getStatusText(TaskStatus status) {
    switch (status) {
      case TaskStatus.generated:
        return '待确认';
      case TaskStatus.confirmed:
        return '已确认';
      case TaskStatus.executing:
        return '执行中...';
      case TaskStatus.completed:
        return '已完成';
      case TaskStatus.failed:
        return '执行失败';
    }
  }

  Color getStatusBgColor(TaskStatus status, bool isDark) {
    switch (status) {
      case TaskStatus.generated:
        return isDark ? const Color(0xFF431407) : const Color(0xFFFFF7ED);
      case TaskStatus.confirmed:
        return isDark ? const Color(0xFF172554) : const Color(0xFFEFF6FF);
      case TaskStatus.executing:
        return isDark ? const Color(0xFF3B0764) : const Color(0xFFFAF5FF);
      case TaskStatus.completed:
        return isDark ? const Color(0xFF052E16) : const Color(0xFFF0FDF4);
      case TaskStatus.failed:
        return isDark ? const Color(0xFF450A0A) : const Color(0xFFFEF2F2);
    }
  }

  Color getStatusTextColor(TaskStatus status, bool isDark) {
    switch (status) {
      case TaskStatus.generated:
        return isDark ? const Color(0xFFFB923C) : const Color(0xFFC2410C);
      case TaskStatus.confirmed:
        return isDark ? const Color(0xFF93C5FD) : const Color(0xFF1D4ED8);
      case TaskStatus.executing:
        return isDark ? const Color(0xFFC084FC) : const Color(0xFF7C3AED);
      case TaskStatus.completed:
        return isDark ? const Color(0xFF4ADE80) : const Color(0xFF15803D);
      case TaskStatus.failed:
        return isDark ? const Color(0xFFF87171) : const Color(0xFFB91C1C);
    }
  }

