  // Widget _buildStatusIndicator(ThemeData theme, bool isDark) { ... } -> Removed/Unused

  // Widget _buildStatusIndicator moved to chat bubble logic

  Widget _buildContentArea(ThemeData theme, bool isDark) {
    // 构建聊天列表项 (正向：从上往下)
    List<Widget> chatItems = [];

    // 1. 历史会话
    for (var session in sessions) {
      // 用户指令 (靠右)
      chatItems.add(_buildUserBubble(
        theme,
        isDark,
        text: session.text,
      ));

      // 系统回复 (任务卡片 - 居中展示)
      if (session.tasks.isNotEmpty) {
        chatItems.add(Padding(
          padding: const EdgeInsets.symmetric(vertical: 8),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.center, // 居中
            children: session.tasks
                .map((task) => Container(
                    constraints: const BoxConstraints(maxWidth: 400),
                    margin: const EdgeInsets.only(bottom: 8),
                    child: _buildTaskCard(task, theme, isDark)))
                .toList(),
          ),
        ));
      }
    }

    // 2. 瞬态气泡 (显示在最底部)
    // 正在录音状态 (靠右)
    if (isRecording) {
      chatItems.add(_buildRightStatusBubble(
        theme,
        isDark,
        text: recordingText.isEmpty ? '正在录音中...' : recordingText,
        iconColor: const Color(0xFF9333EA),
        isShimmer: true,
      ));
    }

    // 正在处理状态 (靠右)
    if (isProcessing) {
      chatItems.add(_buildRightStatusBubble(
        theme,
        isDark,
        text: '正在分析指令...',
        iconColor: const Color(0xFF9333EA),
        isShimmer: true,
      ));
    }

    // 3. 空状态
    if (chatItems.isEmpty) {
      return _buildEmptyState(theme, isDark);
    }

    return ListView.builder(
      controller: _scrollController,
      padding: const EdgeInsets.all(16),
      reverse: false, // 正向展示
      itemCount: chatItems.length,
      itemBuilder: (context, index) {
        return Padding(
          padding: const EdgeInsets.only(bottom: 16),
          child: chatItems[index],
        );
      },
    );
  }

